import scrapyfrom scrapy_splash import SplashRequestclass DiningSpider(scrapy.Spider):    name = "food"        custom_settings = {        'DUPEFILTER_CLASS': 'scrapy_splash.SplashAwareDupeFilter'    }        def start_requests(self):        urls = [            'https://dining.columbia.edu/content/john-jay-dining-hall',            'https://dining.columbia.edu/content/jjs-place-0',            'https://dining.columbia.edu/content/ferris-booth-commons-0'        ]        for url in urls:            yield SplashRequest(url, self.parse, args = {'wait': 1.0})    def parse(self, response):                 url_name = response.url.split('/')[-1].split('-')        if url_name[-1] == '0':            url_name = url_name[0:-1]                    filename = ' '.join(url_name) + '.txt'                with open(filename, 'wb') as f:            r = list(set(response.css('h5::text').getall()))                        for item in r:                self.log(item)                f.write(item.encode())                f.write('\n'.encode())                            if (r is None):                f.write('nothing found'.encode())                        self.log(f'Saved file {filename}')